	function getGeneralData($summary){
		foreach ($summary as $v) {
			if(!empty($v['kda'])){
				$array['kda'] = (isset($array['kda'])) ? $array['kda']+$v['kda'] : $v['kda'];
				$array['pickrate'] = (isset($array['pickrate'])) ? $array['pickrate']+$v['pickrate'] : $v['pickrate'];
				$array['winrate'] = (isset($array['winrate'])) ? $array['winrate']+$v['winrate'] : $v['winrate'];
				$array['kills'] = (isset($array['kills'])) ? $array['kills']+$v['kills'] : $v['kills'];
				$array['deaths'] = (isset($array['deaths'])) ? $array['deaths']+$v['deaths'] : $v['deaths'];
				$array['assists'] = (isset($array['assists'])) ? $array['assists']+$v['assists'] : $v['assists'];
				$array['minions'] = (isset($array['minions'])) ? $array['minions']+$v['minions'] : $v['minions'];
				$array['gold'] = (isset($array['gold'])) ? $array['gold']+$v['gold'] : $v['gold'];
			}
		}
		$all = count($summary)-1;
		foreach ($array as $key => $value) {
			$round = ($key=='minions' || $key=='gold') ? 0 : 2;
			$all2 = ($key=='kda' || $key=='pickrate' || $key=='winrate' ) ? $all : 549470;
			$array['average'.ucfirst($key)]=round($value/$all2,$round);
		}
		return $array;
	}

